insert into testregel (id, krav, testregel_schema, namn, modus, testregel_id, versjon, status, dato_sist_endra, spraak, tema, type, testobjekt, krav_til_samsvar, innhaldstype_testing, krav_id) values (66, '3.3.3 Forslag ved feil', '{
