insert into testregel (id, krav, testregel_schema, namn, modus, testregel_id, versjon, status, dato_sist_endra, spraak, tema, type, testobjekt, krav_til_samsvar, innhaldstype_testing, krav_id) values (36, '1.1.1 Ikke-tekstlig innhold', '{
