insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.1.1 Ikke-tekstlig innhold', 'Gjeldande', '<div class="factbox"><div class="body"><p>Alt ikke-tekstlig innhold som presenteres for brukeren, har et tekstalternativ som har samme formål, med unntak av situasjonene som er beskrevet nedenfor:</p><ul><li><strong>Kontrollelementer, inndata:</strong> Hvis det ikke-tekstlige innholdet er et kontrollelement eller hvis det godtar inndata fra brukere, har det et navn som beskriver formålet. (Under retningslinje 4.1 finnes informasjon om ytterligere krav til kontrollelementer samt innhold som godtar inndata fra brukere.)</li><li><strong>Tidsbaserte medier: </strong>Hvis det ikke-tekstlige innholdet er tidsbaserte medier, må tekstalternativene som et minimum gi en beskrivende identifikasjon av det ikke-tekstlige innholdet. (Under retningslinje 1.2 er det informasjon om ytterligere krav til medier.)</li><li><strong>Test:</strong> Hvis det ikke-tekstlige innholdet er en test eller øvelse som ikke ville gitt mening hvis den ble presentert med tekst, må tekstalternativene som et minimum gi en beskrivende identifikasjon av det ikke-tekstlige innholdet.</li><li><strong>Sanseopplevelse:</strong> Hvis formålet med det ikke-tekstlige innholdet først og fremst er å skape en spesifikk sanseopplevelse, må tekstalternativene som et minimum gi en beskrivende identifikasjon av det ikke-tekstlige innholdet.</li><li><strong>CAPTCHA:</strong> Hvis formålet med det ikke-tekstlige innholdet er å bekrefte at innholdet brukes av en person, og ikke av en datamaskin, skal det gis tekstalternativer som identifiserer og beskriver hensikten med det ikke-tekstlige innholdet. Det skal dessuten legges til rette for ulike typer funksjonsnedsettelser ved at det finnes alternative former for CAPTCHA som bruker utdataformater beregnet på ulike typer sensorisk persepsjon. </li><li><strong>Dekorasjon, formatering, usynlig:</strong> Hvis det ikke-tekstlige innholdet er ren dekorasjon, hvis det utelukkende brukes til visuell formatering, eller hvis det ikke presenteres for brukerne, skal det implementeres på en slik måte at det kan ignoreres av kompenserende teknologi.</li></ul></div></div>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/111-ikke-tekstlig-innhold-niva/87');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.2.1 Bare lyd og bare video (forhåndsinnspilt)', 'Gjeldande', '<p>For forhåndsinnspilt innhold som bare er lyd eller video gjelder følgende, bortsett fra når lyden eller videoen utgjør et mediealternativ til tekst og er tydelig merket som det.</p><ul><li>Forhåndsinnspilt lyd (bare lyd): Det gis tilgang til et alternativ til tidsbaserte medier som presenterer informasjon som tilsvarer det forhåndsinnspilte lydinnholdet.</li><li>Forhåndsinnspilt video (bare video): Det gis tilgang til enten et alternativ til tidsbaserte medier eller et lydspor som presenterer informasjon som tilsvarer det forhåndsinnspilte videoinnholdet.</li></ul>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/121-bare-lyd-og-bare-video-forhandsinnspilt-niva/88');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.2.2 Teksting (forhåndsinnspilt)', 'Gjeldande', '<p>Det gis tilgang til teksting for alt forhåndsinnspilt lydinnhold i synkroniserte medier, bortsett fra når mediene fungerer som mediealternativer til tekst og er tydelig merket som det.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/122-teksting-forhandsinnspilt-niva/89');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.3.1 Informasjon og relasjoner', 'Gjeldande', '<p>Informasjon, struktur og relasjoner som formidles via presentasjonen, kan bestemmes programmeringsmessig eller gjøres tilgjengelig(e) som tekst.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/133-sensoriske-egenskaper-niva/92');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.4.1 Bruk av farge', 'Gjeldande', '<p>Farge blir ikke benyttet som det eneste visuelle virkemiddelet for å formidle informasjon, angi en handling, be om respons eller skille ut et visuelt element.</p><p><em>Merknad: </em>Dette suksesskriteriet handler spesifikt om fargepersepsjon. Andre former for persepsjon omtales under retningslinje 1.3, blant annet programmeringsmessig tilgang til koder for farger og annen visuell presentasjon.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/141-bruk-av-farge-niva/93');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.4.2 Styring av lyd', 'Gjeldande', '<p>Hvis lyd på en webside spilles av automatisk i mer enn 3 sekunder, finnes det enten en mekanisme for å stoppe lyden helt eller midlertidig, eller en mekanisme som kan regulere lydstyrken uavhengig av det generelle systemvolumet.</p><p><em>Merknad: </em>Fordi enhver form for innhold som ikke oppfyller dette suksesskriteriet, kan hindre brukeren i å bruke hele siden, må alt innhold på websiden overholde dette suksesskriteriet (uansett om det brukes til å oppfylle andre suksesskriterier). Se Forutsetninger for overholdelse av krav, punkt 5: Ingen interferens.</p>', false, true, false, 'https://uu.difi.no/krav-og-regelverk/wcag-20-standarden/142-styring-av-lyd-niva');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.4.3 Kontrast (minimum)', 'Gjeldande', '<p>Den visuelle presentasjonen av tekst og bilder av tekst har et kontrastforhold på minst 4.5:1, unntatt i følgende tilfeller:</p><ul><li>Stor tekst: Stor skriftstørrelse og bilder av stor skriftstørrelse har et kontrastforhold på minst 3:1.</li><li>Uvesentlig: Tekst eller bilder av tekst som utgjør en del av en inaktiv brukergrensesnittkomponent, som er ren dekorasjon, som ikke er synlig(e) for noen, eller som utgjør en del av et bilde som inneholder annet vesentlig visuelt innhold, er ikke underlagt kontrastkrav.</li><li>Logoer: Tekst som utgjør en del av en logo eller et varemerkenavn, er ikke underlagt kontrastkrav.</li></ul>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/143-kontrast-minimum-niva-aa/95');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.4.4 Endring av tekststørrelse', 'Gjeldande', '<p>Med unntak av teksting og bilder av tekst kan tekst forstørres opp til 200 % uten bruk av kompenserende teknologi og uten at innhold eller funksjonalitet går tapt.</p>', false, true, false, 'https://uu.difi.no/krav-og-regelverk/wcag-20-standarden/144-endring-av-tekststorrelse-niva-aa');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.4.5 Bilder av tekst', 'Gjeldande', '<p>Hvis teknologien som brukes kan håndtere den visuelle presentasjonen, brukes det tekst i stedet for bilder av tekst til å formidle informasjon, unntatt i følgende tilfeller:</p><ul><li><strong>Kan tilpasses:</strong> Bildet av tekst kan visuelt tilpasses til brukerens krav.</li><li><strong>Nødvendig:</strong> En bestemt presentasjon av tekst er nødvendig for informasjonen som formidles.</li></ul><p><em>Merknad: </em>Logoer (tekst som utgjør en del av en logo eller et varemerkenavn) anses som nødvendige.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/145-bilder-av-tekst-niva-aa/97');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.1.1 Tastatur', 'Gjeldande', '<p>All funksjonalitet i innholdet kan betjenes via et tastaturgrensesnitt uten at det er behov for tidsberegning av de enkelte tastetrykkene, unntatt hvis den underliggende funksjonen krever inndata som er avhengige av rekkefølgen på brukerens bevegelser, og ikke bare av sluttpunktene.</p><p><em>Merknad 1: </em>Dette unntaket gjelder den underliggende funksjonen, ikke inndatateknikken. Hvis teksten for eksempel skrives inn med håndskrift, krever inndatateknikken (håndskrift) inndata i en bestemt rekkefølge, men den underliggende funksjonen (tekstlige inndata) gjør det ikke.</p><p><em>Merknad 2: </em>Dette er ikke et forbud mot og bør ikke hindre bruk av mus eller andre inndatametoder i tillegg til tastatur</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standardardarden/211-tastatur-niva/98');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.1.2 Ingen tastaturfelle', 'Gjeldande', '<p>Hvis tastaturfokus kan flyttes til en av komponentene på siden ved hjelp av et tastaturgrensesnitt, kan fokus flyttes fra den aktuelle komponenten bare ved hjelp av tastaturgrensesnittet. Hvis det er behov for noe annet enn standard pil- eller tabulatortaster eller andre standardmetoder for navigering, får brukeren informasjon om hvilken metode som må benyttes for å flytte fokus.</p><p><em>Merknad: </em>Alt innhold på websiden må overholde dette suksesskriteriet (uansett om det brukes til å oppfylle andre suksesskriterier eller ikke) fordi enhver form for innhold som ikke oppfyller dette suksesskrieriet, kan hindre brukeren i å bruke hele siden. Se Forutsetninger for samsvar med krav, punkt 5: Ingen interferens.</p>', false, true, false, 'https://www.uutilsynet.no/wcadarg-sdarden/212-ingen-tastaturfelle-niva/99');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.2.1 Justerbar hastighet', 'Gjeldande', '<p>For hver tidsbegrensning som er angitt av innholdet, gjelder minst ett av følgende punkter:</p><ul><li><strong>Slå av: </strong>Brukeren kan slå av tidsbegrensningen før den gjør seg gjeldende, eller</li><li><strong>Justere: </strong>Brukeren kan justere tidsbegrensningen før den gjør seg gjeldende, ved hjelp av en skala som er minst ti ganger varigheten av standardinnstillingen, eller</li><li><strong>Forlenge: </strong>Brukeren varsles før tiden utløper, og får minst 20 sekunder til å forlenge tidsbegrensningen ved hjelp av en enkel handling (for eksempel "trykk på mellomromstasten"), og brukeren kan forlenge tidsbegrensningen minst ti ganger, eller</li><li><strong>Unntak i sanntid: </strong>Tidsbegrensningen er en nødvendig del av en hendelse i sanntid (for eksempel en auksjon), og det finnes ikke noe alternativ til tidsbegrensningen, eller</li><li><strong>Nødvendig unntak: </strong>Tidsbegrensningen er nødvendig, og en forlengelse vil gjøre handlingen ugyldig, eller</li><li><strong>20-timers unntak: </strong>Tidsbegrensningen varer lenger enn 20 timer.</li></ul><p><em>Merknad: </em>Dette suksesskriteriet bidrar til å sikre at brukerne kan utføre oppgaver uten at det oppstår uforutsette endringer i innhold eller kontekst som er forårsaket av en tidsbegrensning. Dette suksesskriteriet bør ses i sammenheng med suksesskriterium 3.2.1, som setter begrensninger på endringer i innhold eller kontekst forårsaket av brukerhandlinger.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/221-justerbar-hastighet-niva/100');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.2.2 Pause, stopp, skjul', 'Gjeldande', '<p>For bevegelse, rulling eller automatisk oppdatering av informasjon gjelder begge de følgende punkter:</p><ul><li><strong>Bevegelse, blinking, rulling: </strong>Ved enhver form for bevegelse, blinking eller rulling av informasjon som (1) starter automatisk, (2) varer i mer enn 5 sekunder og (3) presenteres samtidig med annet innhold, finnes det en mekanisme som brukeren kan benytte til å sette den på pause, stoppe eller skjule den, med mindre bevegelsen, blinkingen eller rullingen er del av en handling der den er nødvendig.</li><li><strong>Automatisk oppdatering: </strong>Ved enhver form for automatisk oppdatering av informasjon som (1) starter automatisk og (2) presenteres samtidig med annet innhold, finnes det en mekanisme som brukeren kan benytte til å sette den på pause, stoppe eller skjule den, eller til å styre oppdateringsfrekvensen, med mindre den automatiske oppdateringen er del av en handling der den er nødvendig.</li></ul><p><em>Merknad 1: </em>Informasjon om krav som gjelder flimrende eller blinkende innhold, omtales i retningslinje 2.3.</p><p><em>Merknad 2: </em>Alt innhold på websiden må overholde dette suksesskriteriet (uansett om det brukes til å oppfylle andre suksesskriterier eller ikke) fordi enhver form for innhold som ikke oppfyller dette suksesskriteriet, kan hindre brukeren i å bruke hele siden. Se Forutsetninger for overholdelse av krav, punkt 5: Ingen interferens.</p><p><em>Merknad 3: </em>Innhold som oppdateres regelmessig av programvare, eller som spilles av direkte til brukeragenten, behøver ikke å kunne lagre eller presentere informasjon som genereres eller mottas fra pausen begynner til presentasjonen gjenopptas fordi dette ikke alltid er teknisk mulig og fordi det i mange situasjoner ville vært misvisende.</p><p><em>Merknad 4: </em>En animasjon som inngår i forhåndsinnlasting eller lignende, kan anses som nødvendig hvis det ikke kan forekomme interaksjon for noen brukere i denne fasen, og hvis mangel på en framdriftsindikator kunne forvirre brukerne og skape inntrykk av at innholdet er enten frosset eller ødelagt.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/222-pause-stjuloppjul-niva/101');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.3.1 Terskelverdi på maksimalt tre glimt', 'Gjeldande', '<p>Websider har ikke innhold som glimter mer enn tre ganger i løpet av ett sekund, eller glimt er innenfor terskelverdiene for generelle glimt og røde glimt.</p><p><em>Merknad: </em>Fordi enhver form for innhold som ikke oppfyller dette suksesskriteriet kan hindre brukeren i å bruke hele siden, må alt innhold på websiden overholde dette suksesskriteriet (uansett om det brukes til å oppfylle andre suksesskriterier). Se Forutsetninger for samsvar med punkt 5: Ingen interferens.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/231elv-teelverdi-pa-maksimalt-tre-glimt-niva/102');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.4.1 Hoppe over blokker', 'Gjeldande', '<p>Det finnes en mekanisme for å omgå blokker med innhold som gjentas på flere websider.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/241-hoppe-over-blokker-niva/103');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.4.2 Sidetitler ', 'Gjeldande', '<p>Websider har titler som beskriver den aktuelle sidens emne eller formål.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/242-sidetitler-niva/104');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.4.3 Fokusrekkefølge ', 'Gjeldande', '<p>Hvis en webside kan navigeres sekvensielt og navigeringssekvensen påvirker betydning eller betjening, får fokuserbare komponenter fokus i en rekkefølge som ivaretar betydningen og betjeningen.</p>', false, true, false, 'https://wwwsyn.uusynet.no/wcag-standarden/243-fokusrekkefolge-niva/105');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.4.4 Formål med lenke (i kontekst)', 'Gjeldande', '<p>Formålet med hver lenke kan fastslås ut fra bare selve lenken eller ut fra lenketeksten kombinert med programmeringsmessig bestemt lenkekontekst. Unntaket er hvis formålet med lenken ville vært flertydig for alle brukere.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/244-formal-med-lenke-i-kontekst-niva/106');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.4.5 Flere måter', 'Gjeldande', '<p>Det finnes mer enn én måte å finne frem til en webside på innenfor et sett av websider. Unntaket er hvis websiden utgjør resultatet av, eller et trinn i, en prosess.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/245-flere-mater-niva-aa/107');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.4.6 Overskrifter og ledetekster', 'Gjeldande', '<p>Overskrifter og ledetekster beskriver emne eller formål.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/246-overskrifter-og-ledetekster-niva-aa/108');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('3.1.1 Språk på siden', 'Gjeldande', '<p>Standard naturlig språk på hver webside kan bestemmes programmeringsmessig.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/311-sprak-pa-siden-niva/110');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('3.1.2 Språk på deler av innhold', 'Gjeldande', '<p>Naturlig språk i de enkelte avsnittene eller setningene som innholdet består av, kan bestemmes programmeringsmessig. Unntaket er egennavn, tekniske termer, ord av ubestemmelig språklig opphav samt ord eller uttrykk som har blitt en del av språket i den omkringliggende teksten.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/312-sprak-pa-deler-av-innhold-niva-aa/111');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('3.2.1 Fokus', 'Gjeldande', '<p>Når en komponent kommer i fokus, medfører det ikke kontekstendring.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/321-fokus-niva/112');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('3.2.2 Inndata', 'Gjeldande', '<p>Endring av innstillingene til en brukergrensesnittkomponent medfører ikke automatisk kontekstendring med mindre brukeren er blitt varslet om det før bruk av komponenten.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/322-inndata-niva/114');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('3.2.3 Konsekvent navigering ', 'Gjeldande', '<p>Navigeringsmekanismer som gjentas på flere websider innenfor et sett av websider, opptrer i samme relative rekkefølge hver gang de gjentas, med mindre brukeren selv foretar en endring.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/323-konsekvent-navigering-niva-aa/113');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('3.2.4 Konsekvent identifikasjon', 'Gjeldande', '<p>Komponenter som har samme funksjonalitet innenfor en samling av websider, identifiseres på en konsekvent måte.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/324-konsekvent-identifikasjon-niva-aa/115');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('3.3.1 Identifikasjon av feil', 'Gjeldande', '<p>Hvis en inndatafeil oppdages automatisk, identifiseres elementet som feilen berører, og brukeren får en tekstbeskrivelse av feilen.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/331-identifikasjon-av-feil-niva/116');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('3.3.2 Ledetekster eller instruksjoner', 'Gjeldande', '<p>Det vises ledetekster eller instruksjoner når innholdet krever inndata fra brukeren.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/332-ledetekster-eller-instruksjoner-niva/117');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('3.3.3 Forslag ved feil', 'Gjeldande', '<p>Hvis en inndatafeil oppdages automatisk og det finnes forslag til hvordan den kan rettes, presenteres forslagene for brukeren, med mindre dette innebærer risiko for sikkerheten eller formålet med innholdet.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/333-forslag-ved-feil-niva-aa/118');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('3.3.4 Forhindring av feil (juridiske feil, økonomiske feil, datafeil)', 'Gjeldande', '<p>For websider som medfører juridiske forpliktelser eller krever økonomiske transaksjoner fra brukeren, som endrer eller sletter brukerstyrte data i datalagringssystemer, eller som sender svar på tester utført av brukeren, gjelder minst ett av følgende punkter:</p><ol><li><strong>Reverserbarhet:</strong> Sendeprosesser kan reverseres.</li><li><strong>Kontroll:</strong> Det kontrolleres om data som angis av brukeren, inneholder inndatafeil, og brukeren gis mulighet til å rette opp eventuelle feil.</li><li><strong>Bekreftelse:</strong> Det finnes en mekanisme for gjennomgang, bekreftelse og oppretting av informasjon før den sendes.</li></ol>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/334-forhindring-av-feil-juridiske-feil-okonomiske-feil-datafeil-niva-aa/119');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('4.1.1 Parsing (oppdeling)', 'Gjeldande', '<p>I innhold som implementeres ved hjelp av oppmerkingsspråk, har elementene fullstendige start- og sluttkoder, elementene er nøstet i henhold til spesifikasjonene, elementene inneholder ikke dupliserte attributter, og eventuelle ID-er er unike. Unntaket er hvis spesifikasjonene tillater disse funksjonene.</p><p><em>Merknad:</em> Start- og sluttmarkører som mangler et nødvendig tegn, for eksempel avsluttende hakeparentes, eller som har feil angivelse av anførselstegn for attributtverdier, er ikke fullstendige.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/411-parsing-oppdeling-niva/120');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('4.1.2 Navn, rolle, verdi', 'Gjeldande', '<p>For alle brukergrensesnittkomponenter (blant annet skjemaelementer, lenker og komponenter som genereres ved hjelp av skript), kan navn og rolle bestemmes programmeringsmessig. Tilstander, egenskaper og verdier som kan angis av brukeren, kan angis programmeringsmessig, og informasjon om endringer i disse elementene er tilgjengelig for brukeragenter, inkludert kompenserende teknologi.</p><p><em>Merknad: </em>Dette suksesskriteriet er primært beregnet på produsenter av webinnhold som utvikler eller lager skript til egne brukergrensesnittkomponenter. Standard HTML-kontrollelementer oppfyller for eksempel allerede dette suksesskriteriet når de brukes i henhold til spesifikasjonen.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/412-navn-rolle-verdi-niva/121');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.3.2 Meningsfylt rekkefølge', 'Gjeldande', '<p>Når rekkefølgen som innholdet presenteres i, påvirker meningsinnholdet, kan en korrekt leserekkefølge bestemmes programmeringsmessig.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/132-meningsfylt-rekkefolge-niva/91');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.3.3 Sensoriske egenskaper', 'Gjeldande', '<p>Instruksjoner som gjelder for forståelse og betjening av innhold, er ikke utelukkende avhengige av komponentenes sensoriske egenskaper, for eksempel form, størrelse, visuell plassering, orientering eller lyd.</p><p><em>Merknad: </em>Mer informasjon om krav som gjelder farge, finnes under retningslinje 1.4.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/133-sensoriske-egenskaper-niva/92');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.4.7 Synlig fokus', 'Gjeldande', '<p>Tastaturbetjente brukergrensesnitt har en betjeningsmodus der fokusindikatoren for tastaturet er synlig.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/247-synlig-fokus-niva-aa/109');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.3.4 Visningsretning', 'Gjeldande', '<p>Innholdet begrenser ikke visning og bruk til en bestemt visningsretning, som stående eller liggende, med mindre en bestemt visningsretning er avgjørende.</p><p><em>Merknad: </em>Eksempler på at en bestemt visningsretning kan være avgjørende er en sjekk (bank), en piano-app, lysark for en projektor eller TV, eller innhold i virtuell virkelighet der endring av visningsmodus ikke er aktuelt.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/134-visningsretning-niva-aa/141');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.3.5 Identifiser formål med inndata', 'Gjeldande', '<p>Formålet med hvert skjemaelement som samler inn informasjon om brukeren, kan bestemmes programmeringsmessig når</p><ul><li>skjemaelementet tjener et formål som er en del av kapittelet <a href="https://www.w3.org/TR/WCAG21/#input-purposes">inndataformål for brukergrensesnittkomponenter (engelsk)</a>; og</li><li>innholdet er implementert ved hjelp av teknologier med støtte for å identifisere det forventede meningsinnholdet til inndata i skjema</li></ul>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/135-identifiser-formal-med-inndata-niva-aa/142');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.4.10 Dynamisk tilpasning (Reflow)', 'Gjeldande', '<p>Innholdet kan presenteres uten tap av informasjon eller funksjonalitet og uten at brukeren må rulle i to dimensjoner ved</p><ul><li>innhold som ruller loddrett med en bredde som tilsvarer 320 CSS-piksler</li><li>innhold som ruller vannrett med en høyde som tilsvarer 256 CSS-piksler</li></ul><p>Deler av innholdet som krever rulling i to dimensjoner for å ivareta bruk eller meningsinnhold er unntatt.</p><p><strong>Merk:</strong> 320 CSS-piksler tilsvarer en presentasjonsramme som er 1280 CSS-piksler bred ved 400 prosent forstørring. For webinnhold som er utformet for å rulle vannrett (for eksempel med vannrett tekst), tilsvarer 256 CSS-piksler en presentasjonsramme som er 1024 piksler høy ved 400 prosent forstørring.</p><p><strong>Merk:</strong> Eksempler på innhold som krever todimensjonalt layout er bilder, kart, diagram, video, spill, presentasjoner, datatabeller og grensesnitt der det er nødvendig med synlige verktøylinjer mens brukeren endrer på innholdet.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/1410-dynamisk-tilpasning-reflow-niva-aa/144');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.4.11 Kontrast for ikke-tekstlig innhold', 'Gjeldande', '<p>Den visuelle presentasjonen av det følgende har et kontrastforhold på minst 3:1 mot farge(r) som ligger siden av.</p><ul><li>Brukergrensesnittkomponenter: Visuell informasjon som kreves for å identifisere brukergrensesnittkomponenter og tilstander, unntatt inaktive komponenter eller der utseendet på komponenten er bestemt av brukeragenten og ikke endret av produsenter av webinnhold.</li><li>Grafiske objekter: Deler av grafikk som kreves for å forstå innholdet, unntatt tilfeller der en bestemt presentasjon av grafikk er nødvendig for informasjonen som blir formidlet.</li></ul>', false, true, false, null);
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.4.12 Tekstavstand', 'Gjeldande', '<p>For innhold som implementeres ved hjelp av oppmerkingsspråk som støtter de følgende tekstegenskapene (text style properties), oppstår det ikke noe tap av innhold eller funksjonalitet ved å angi alle de følgende egenskapene og uten å endre andre stilegenskaper.</p><ul><li>Linjehøyde (linjeavstand) angis til minst 1,5 ganger skriftstørrelsen.</li><li>Avstand etter avsnitt angis til minst 2 ganger skriftstørrelsen.</li><li>Avstanden mellom bokstaver i blokker av tekst (sperring) angis til minst 0,12 ganger skriftstørrelsen.</li><li>Avstanden mellom ord angis til minst 0,16 ganger skriftstørrelsen.</li></ul><p>Unntak: Menneskelige språk eller skripter som ikke bruker en eller flere av disse tekstegenskapene i skriftspråket, kan oppnå samsvar med kravet med å kun bruke tekstegenskapene som finnes for den bestemte kombinasjonen av språk og skript.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/1412-tekstavstand-niva-aa/146');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.4.13 Pekerfølsomt innhold eller innhold ved tastaturfokus', 'Gjeldande', '<p>Når innhold kommer til syne og så forsvinner ved at man holder musepekeren over innholdet og så flytter musepekeren vekk, eller ved at man flytter tastaturfokus til innholdet og deretter flytter fokus vekk, gjelder alle de følgende punkter:</p><ul><li>Mulig å avvise: Det finnes en mekanisme for å avvise, eller lukke, det ekstra innholdet uten å flytte musepekeren eller tastaturfokuset, unntatt der det ekstra innholdet formidler en inndatafeil eller det ikke forstyrrer eller erstatter annet innhold.</li><li>Mulig å bruke med å holde musepekeren over innholdet: Dersom ekstra innhold kommer til syne ved at man holder musepekeren over innholdet, kan pekeren flyttes over det ekstra innholdet uten at innholdet forsvinner.</li><li>Vedvarende: Det ekstra innholdet forblir synlig helt til brukeren flytter musepekeren eller fokus, brukeren avviser innholdet eller informasjonen ikke lenger er gyldig.</li></ul><p>Unntak: Kravet gjelder ikke når den visuelle presentasjonen er kontrollert av brukeragenten og ikke endret av produsenter av webinnhold.</p><p><strong>Merk:</strong> Eksempel på ekstra innhold som er kontrollert av brukeragenten omfatter verktøytips (tooltip) som kommer av bruk av title-attributtet i HTML.</p><p><strong>Merk:</strong> Egendefinerte verktøytips, undermenyer og annet ekstra innhold som ikke er modalvindu (nonmodal popups) som er pekerfølsomt eller kommer til syne ved tastaturfokus, er eksempler på innhold som er omfattet av dette kriteriet.</p>', false, true, false, null);
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.1.4 Hurtigtaster som består av ett tegn', 'Gjeldande', '<p>Dersom en hurtigtast er implementert i innholdet ved å bruke kun en bokstav (inklusive store og små bokstaver), tegnsetting, siffer eller symboltegn, gjelder minst ett av følgende punkter:</p><ul><li>Slå av: Det finnes en mekanisme for å slå av hurtigtasten.</li><li>Omadressere: Det finnes en mekanisme for å omadressere hurtigtasten til å bruke en eller flere modifiseringstaster (for eksempel Ctrl eller Alt).</li><li>Aktiv kun ved fokus: Hurtigtasten for en brukergrensesnittkomponent er kun aktiv når komponenten kommer i fokus.</li></ul>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/214-hurtigtaster-som-bestar-av-ett-tegn-niva/782');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.5.1 Pekerbevegelser', 'Gjeldande', '<p>All funksjonalitet som bruker flerpunkt eller stibaserte gester for betjening, kan betjenes med en enkelt peker uten stibasert gest, med mindre flerpunkt eller en stibasert gest er nødvendig.</p><p>Merk: Dette kravet gjelder webinnhold som tolker pekerbevegelser (det vil si at dette kravet gjelder ikke handlinger som blir brukt for å betjene brukeragenten eller kompenserende teknologi).</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/251-pekerbevegelser-niva/148');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.5.2 Pekeravbrytelse', 'Gjeldande', '<p>For funksjonalitet som kan betjenes med en enkelt peker, gjelder minst ett av følgende punkt:</p><ul><li>Ingen ned-hendelse (down-event): Pekerens ned-hendelse er ikke brukt for å utføre noen del av funksjonen.</li><li>Avbryte eller angre: Funksjonen fullføres på opp-hendelsen (upevent), og det finnes en mekanisme for å avbryte funksjonen før den fullføres eller for å angre funksjonen etter at den er fullført.</li><li>Opp-reversering: Opp-hendelsen reverserer resultatet av den foregående ned-hendelsen.</li><li>Nødvendig: Det er nødvendig å fullføre funksjonen på nedhendelsen.</li></ul><p><strong>Merk:</strong> Funksjoner som etterligner et tastatur eller talltastatur, anses som nødvendige.</p><p><strong>Merk:</strong> Dette kravet gjelder webinnhold som tolker pekerbevegelser (det vil si at dette kravet gjelder ikke handlinger som blir brukt for å betjene brukeragenten eller kompenserende teknologi).</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/252-pekeravbrytelse-niva/149');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.5.3 Ledetekst i navn', 'Gjeldande', '<p>For <a class="internalDFN" href="httpttps://www.w3.org/TR/WCAG21/#dfn-user-interface-componentsata-link-type=n">user interface components</a> with <a class="internalDFN" href="https://www.w3.org/TR/WCAG21/#dfn-labelsata-link-typdfne=labels</a> that include <a class="internalDFN" href="https:https:https:https:https://www.w3.org/TR/WCAG21/#dfn-text-link-type=textrass="internalDFN" href="https:https:https:https:https://www.w3.org/-teTR/WCA-teG21/#d-tefn-ima-textnk-type=ges/a>, the <a class="internalDFN" href="https://whttps://whttps://whttps://www.w3.org/TR/WCAG21/#dfn-namenk-type=eains the text that is presented visually.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/253-ledetekst-i-navn-niva/150');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('2.5.4 Bevegelsesaktivering', 'Gjeldande', '<p>Funksjonalitet som kan betjenes med å bevege enheten eller gjennom brukerbevegelser, kan også betjenes med brukergrensesnittkomponenter og kan slås av for å forhindre utilsiktet aktivering, unntatt ved følgende punkter:</p><ul><li>Støttet grensesnitt: Bevegelsen er brukt for å betjene funksjonalitet gjennom et grensesnitt som støtter tilgjengelighet.</li><li>Nødvendig: Bevegelsen er nødvendig for funksjonaliteten, og det vil gjøre aktiviteten ugyldig.</li></ul>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/254-bevegelsesaktivering-niva/151');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('4.1.3 Statusbeskjeder', 'Gjeldande', '<p>I innhold som implementeres ved hjelp av oppmerkingsspråk, kan statusbeskjeder bestemmes programmeringsmessig gjennom rolle eller egenskaper, slik at de kan presenteres for brukeren av kompenserende teknologi uten å få fokus.</p>', false, true, false, 'https:https:https://www.uutilsynet.no/wcag-standarden/413-statusbeskjeder-niva-aa/152');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.2.5 Synstolking (forhåndsinnpilt)', 'Ny', '<p>Det gis tilgang til synstolking for alt forhåndsinnspilt videoinnhold i synkroniserte medier.</p>', false, true, false, 'https://www.uutilsynet.no/wcag-standarden/oppbygging-av-wcag-20/139');
insert into  testlab2_krav.krav (tittel, status, innhald, gjeldautomat, gjeldnettsider, gjeldapp, urlrettleiing) values ('1.2.3 Synstolking eller mediealternativ', 'Ny', '<p>Det gis tilgang til et <a class="termref" title="definisjon: alternativ for tidsbaserte medier" href="https://www.w3.org/Translations/WCAG20-no/#alt-time-based-mediadeflternativl tidsbaserte medier</a> eller <a class="termref" title="definisjon: synstolking" href="https:https:https:https:https:https://www.w3.org/Translations/WCAG20-no/#audiodescdeftolkingvass=efe="definisjon: forhåndsinnspilt" href="https:https:https:httansps://wansww.w3.anslations/WCAG20-no/#prerecordeddefsinnspiltlass="termref" title="definisjon: video" href="https://whttps://whttps://whttps://whttps://www.w3.org/Translations/WCAG20-no/#videodefnholdetlermrefinisjon: synkroniserte medier" href="https://www.https://www.https://www.https://www.w3.org/Translations/WCAG20-no/#synchronizedmediadefrtea når mediene fungerer som <a class="termref" title="definisjon: mediealternativ for tekst" href="https://www.w3.https://www.w3.https://www.w3.org/Translations/WCAG20-no/#multimedia-alt-textdefiverog er tydelig merket som det. (Nivå A)</p>', false, true, false, 'https://www.uutilshttps://www.uutilsynet.no/wcag-standarden/123-synstolking-eller-mediealternativ-forhandsinnspilt-niva/841');